import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, BarChart, Bar, PieChart, Pie, Cell } from 'recharts';
import { User, Wallet, TrendingUp, Plus, Edit2, Trash2, Eye, EyeOff, LogOut, DollarSign, ArrowUpRight, ArrowDownRight, Menu, X, ShoppingCart, TrendingDown, History } from 'lucide-react';

const API_BASE_URL = 'http://localhost:8000';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [users, setUsers] = useState([]);
  const [accounts, setAccounts] = useState([]);
  const [stocks, setStocks] = useState([]);
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [accountBalances, setAccountBalances] = useState({});
  const [accountTrades, setAccountTrades] = useState([]);
  const [accountPositions, setAccountPositions] = useState([]);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [showPassword, setShowPassword] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // Modal states
  const [showUserModal, setShowUserModal] = useState(false);
  const [showAccountModal, setShowAccountModal] = useState(false);
  const [showTradeModal, setShowTradeModal] = useState(false);
  const [showDepositModal, setShowDepositModal] = useState(false);
  const [tradeMode, setTradeMode] = useState('BUY_STOCK'); // BUY_STOCK or SELL_STOCK
  const [editingUser, setEditingUser] = useState(null);
  const [editingAccount, setEditingAccount] = useState(null);

  // Form states
  const [userForm, setUserForm] = useState({ name: '', email: '', password: '', type: 'user' });
  const [accountForm, setAccountForm] = useState({ name: '', user_id: '' });
  const [tradeForm, setTradeForm] = useState({ account_id: '', stock_id: '', quantity: '', price: '', description: '' });
  const [depositForm, setDepositForm] = useState({ amount: '', description: '' });

  // Loading and error states
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (currentUser) {
      fetchData();
    }
  }, [currentUser]);

  useEffect(() => {
    if (selectedAccount) {
      fetchAccountDetails(selectedAccount.id);
    }
  }, [selectedAccount]);

  const fetchData = async () => {
    setLoading(true);
    try {
      const [usersRes, accountsRes, stocksRes] = await Promise.all([
        fetch(`${API_BASE_URL}/users/`),
        fetch(`${API_BASE_URL}/accounts/`),
        fetch(`${API_BASE_URL}/stocks/stocks`)
      ]);

      if (!usersRes.ok || !accountsRes.ok || !stocksRes.ok) {
        throw new Error('Failed to fetch data');
      }

      const usersData = await usersRes.json();
      const accountsData = await accountsRes.json();
      const stocksData = await stocksRes.json();

      setUsers(usersData);
      setAccounts(accountsData);
      setStocks(stocksData);

      // Fetch balances for user accounts
      const userAccountsData = accountsData.filter(a => a.user_id === currentUser.id);
      await fetchAccountBalances(userAccountsData);

      setError(null);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load data. Make sure the backend is running.');
    } finally {
      setLoading(false);
    }
  };

  const fetchAccountBalances = async (userAccounts) => {
    const balances = {};
    for (const account of userAccounts) {
      try {
        const response = await fetch(`${API_BASE_URL}/trades/account/${account.id}/balance`);
        if (response.ok) {
          const data = await response.json();
          balances[account.id] = data.balance;
        }
      } catch (error) {
        console.error(`Error fetching balance for account ${account.id}:`, error);
      }
    }
    setAccountBalances(balances);
  };

  const fetchAccountDetails = async (accountId) => {
    try {
      const [tradesRes, positionsRes] = await Promise.all([
        fetch(`${API_BASE_URL}/trades/account/${accountId}`),
        fetch(`${API_BASE_URL}/positions/account/${accountId}`)
      ]);

      if (tradesRes.ok) {
        const tradesData = await tradesRes.json();
        setAccountTrades(tradesData);
      }

      if (positionsRes.ok) {
        const positionsData = await positionsRes.json();
        setAccountPositions(positionsData);
      }
    } catch (error) {
      console.error('Error fetching account details:', error);
    }
  };

  const handleLogin = async (email, password) => {
    try {
      const response = await fetch(`${API_BASE_URL}/users/`);
      if (!response.ok) throw new Error('Failed to fetch users');

      const allUsers = await response.json();
      const user = allUsers.find(u => u.email === email);

      if (user && user.password === password) {
        setCurrentUser(user);
        setActiveTab('dashboard');
        setError(null);
      } else {
        alert('Invalid email or password');
      }
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed. Make sure the backend is running on http://localhost:8000');
    }
  };

  const handleCreateUser = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/users/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userForm)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Failed to create user');
      }

      const newUser = await response.json();
      setUsers([...users, newUser]);
      setShowUserModal(false);
      setUserForm({ name: '', email: '', password: '', type: 'user' });

      if (!currentUser) {
        setCurrentUser(newUser);
        setActiveTab('dashboard');
      }
    } catch (error) {
      console.error('Error creating user:', error);
      alert(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateUser = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const updateData = {};
      if (userForm.name) updateData.name = userForm.name;
      if (userForm.email) updateData.email = userForm.email;
      if (userForm.password) updateData.password = userForm.password;

      const response = await fetch(`${API_BASE_URL}/users/${editingUser.id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updateData)
      });

      if (!response.ok) throw new Error('Failed to update user');

      const updatedUser = await response.json();
      setUsers(users.map(u => u.id === updatedUser.id ? updatedUser : u));

      if (currentUser.id === updatedUser.id) {
        setCurrentUser(updatedUser);
      }

      setShowUserModal(false);
      setEditingUser(null);
      setUserForm({ name: '', email: '', password: '', type: 'user' });
    } catch (error) {
      console.error('Error updating user:', error);
      alert('Failed to update user');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteUser = async (userId) => {
    if (!window.confirm('Are you sure you want to delete this user? All associated accounts will be deleted.')) return;

    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/users/${userId}`, {
        method: 'DELETE'
      });

      if (!response.ok) throw new Error('Failed to delete user');

      setUsers(users.filter(u => u.id !== userId));

      if (currentUser.id === userId) {
        setCurrentUser(null);
      }
    } catch (error) {
      console.error('Error deleting user:', error);
      alert('Failed to delete user');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateAccount = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/accounts/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          name: accountForm.name, 
          user_id: parseInt(accountForm.user_id)
        })
      });

      if (!response.ok) throw new Error('Failed to create account');

      const newAccount = await response.json();
      setAccounts([...accounts, newAccount]);
      setShowAccountModal(false);
      setAccountForm({ name: '', user_id: '' });
      await fetchData();
    } catch (error) {
      console.error('Error creating account:', error);
      alert('Failed to create account');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateAccount = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/accounts/${editingAccount.id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: accountForm.name })
      });

      if (!response.ok) throw new Error('Failed to update account');

      const updatedAccount = await response.json();
      setAccounts(accounts.map(a => a.id === updatedAccount.id ? updatedAccount : a));
      setShowAccountModal(false);
      setEditingAccount(null);
      setAccountForm({ name: '', user_id: '' });
    } catch (error) {
      console.error('Error updating account:', error);
      alert('Failed to update account');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteAccount = async (accountId) => {
    if (!window.confirm('Are you sure you want to delete this account?')) return;

    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/accounts/?account_id=${accountId}`, {
        method: 'DELETE'
      });

      if (!response.ok) throw new Error('Failed to delete account');

      setAccounts(accounts.filter(a => a.id !== accountId));
      await fetchData();
    } catch (error) {
      console.error('Error deleting account:', error);
      alert('Failed to delete account');
    } finally {
      setLoading(false);
    }
  };

  const handleDeposit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/trades/money`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          account_id: selectedAccount.id,
          type: 'DEPOSIT',
          amount: parseFloat(depositForm.amount),
          description: depositForm.description || 'Deposit'
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Failed to deposit');
      }

      setShowDepositModal(false);
      setDepositForm({ amount: '', description: '' });
      await fetchData();
      await fetchAccountDetails(selectedAccount.id);
      alert('Deposit successful!');
    } catch (error) {
      console.error('Error depositing:', error);
      alert(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleTrade = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/trades/stocks`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          account_id: parseInt(tradeForm.account_id),
          stock_id: parseInt(tradeForm.stock_id),
          type: tradeMode,
          quantity: parseFloat(tradeForm.quantity),
          price: parseFloat(tradeForm.price),
          description: tradeForm.description || `${tradeMode === 'BUY_STOCK' ? 'Buy' : 'Sell'} stock`
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Trade failed');
      }

      setShowTradeModal(false);
      setTradeForm({ account_id: '', stock_id: '', quantity: '', price: '', description: '' });
      await fetchData();
      if (selectedAccount) {
        await fetchAccountDetails(selectedAccount.id);
      }
      alert('Trade executed successfully!');
    } catch (error) {
      console.error('Error executing trade:', error);
      alert(error.message);
    } finally {
      setLoading(false);
    }
  };

  const openEditUserModal = (user) => {
    setEditingUser(user);
    setUserForm({ name: user.name, email: user.email, password: '', type: user.type });
    setShowUserModal(true);
  };

  const openEditAccountModal = (account) => {
    setEditingAccount(account);
    setAccountForm({ name: account.name, user_id: account.user_id });
    setShowAccountModal(true);
  };

  const openCreateUserModal = () => {
    setEditingUser(null);
    setUserForm({ name: '', email: '', password: '', type: 'user' });
    setShowUserModal(true);
  };

  const openCreateAccountModal = () => {
    setEditingAccount(null);
    setAccountForm({ name: '', user_id: currentUser?.id || '' });
    setShowAccountModal(true);
  };

  const openTradeModal = (mode, account = null) => {
    setTradeMode(mode);
    setTradeForm({ 
      account_id: account?.id || selectedAccount?.id || '', 
      stock_id: '', 
      quantity: '', 
      price: '', 
      description: '' 
    });
    setShowTradeModal(true);
  };

  const openDepositModal = (account) => {
    setSelectedAccount(account);
    setDepositForm({ amount: '', description: '' });
    setShowDepositModal(true);
  };

  // Login Screen
  if (!currentUser) {
    return (
      <>
        <LoginScreen onLogin={handleLogin} onSignup={openCreateUserModal} />
        {showUserModal && (
          <UserModal
            user={null}
            form={userForm}
            setForm={setUserForm}
            onSubmit={handleCreateUser}
            onClose={() => {
              setShowUserModal(false);
              setUserForm({ name: '', email: '', password: '', type: 'user' });
            }}
            showPassword={showPassword}
            setShowPassword={setShowPassword}
            loading={loading}
          />
        )}
      </>
    );
  }

  const userAccounts = accounts.filter(a => a.user_id === currentUser.id);
  const totalBalance = Object.values(accountBalances).reduce((sum, balance) => sum + balance, 0);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="bg-gradient-to-br from-blue-600 to-blue-700 p-2 rounded-lg shadow-lg">
                <DollarSign className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-xl sm:text-2xl font-bold text-gray-900">FinTech Pro</h1>
                <p className="text-xs text-gray-500 hidden sm:block">Investment Management</p>
              </div>
            </div>

            <div className="flex items-center space-x-2 sm:space-x-4">
              <div className="text-right hidden sm:block">
                <p className="text-sm text-gray-500">Welcome back</p>
                <p className="font-semibold text-gray-900">{currentUser.name}</p>
              </div>
              <button
                onClick={() => setCurrentUser(null)}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition"
                title="Logout"
              >
                <LogOut className="w-5 h-5" />
              </button>
              <button
                onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition sm:hidden"
              >
                {mobileMenuOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className={`bg-white border-b border-gray-200 ${mobileMenuOpen ? 'block' : 'hidden'} sm:block`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex flex-col sm:flex-row sm:space-x-8">
            {['dashboard', 'accounts', 'trading', 'stocks', 'profile'].map(tab => (
              <button
                key={tab}
                onClick={() => {
                  setActiveTab(tab);
                  setMobileMenuOpen(false);
                }}
                className={`py-4 px-1 border-b-2 font-medium text-sm transition capitalize ${
                  activeTab === tab
                    ? 'border-blue-600 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                {tab}
              </button>
            ))}
          </div>
        </div>
      </nav>

      {/* Error Banner */}
      {error && (
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
          <div className="bg-red-50 border border-red-200 text-red-800 px-4 py-3 rounded-lg">
            {error}
          </div>
        </div>
      )}

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {loading && (
          <div className="text-center py-8">
            <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
            <p className="text-gray-500 mt-2">Loading...</p>
          </div>
        )}

        {!loading && activeTab === 'dashboard' && (
          <DashboardTab
            userAccounts={userAccounts}
            totalBalance={totalBalance}
            stocks={stocks}
            accountBalances={accountBalances}
            accountTrades={accountTrades}
            onSelectAccount={setSelectedAccount}
          />
        )}
        {!loading && activeTab === 'accounts' && (
          <AccountsTab
            accounts={userAccounts}
            accountBalances={accountBalances}
            onCreateAccount={openCreateAccountModal}
            onEditAccount={openEditAccountModal}
            onDeleteAccount={handleDeleteAccount}
            onDeposit={openDepositModal}
            onSelectAccount={(account) => {
              setSelectedAccount(account);
              setActiveTab('trading');
            }}
          />
        )}
        {!loading && activeTab === 'trading' && (
          <TradingTab
            accounts={userAccounts}
            stocks={stocks}
            selectedAccount={selectedAccount}
            setSelectedAccount={setSelectedAccount}
            accountTrades={accountTrades}
            accountPositions={accountPositions}
            accountBalances={accountBalances}
            onTrade={openTradeModal}
            onDeposit={openDepositModal}
          />
        )}
        {!loading && activeTab === 'stocks' && <StocksTab stocks={stocks} onTrade={openTradeModal} accounts={userAccounts} />}
        {!loading && activeTab === 'profile' && (
          <ProfileTab
            user={currentUser}
            onEdit={() => openEditUserModal(currentUser)}
            onDelete={() => handleDeleteUser(currentUser.id)}
          />
        )}
      </main>

      {/* Modals */}
      {showUserModal && (
        <UserModal
          user={editingUser}
          form={userForm}
          setForm={setUserForm}
          onSubmit={editingUser ? handleUpdateUser : handleCreateUser}
          onClose={() => {
            setShowUserModal(false);
            setEditingUser(null);
          }}
          showPassword={showPassword}
          setShowPassword={setShowPassword}
          loading={loading}
        />
      )}

      {showAccountModal && (
        <AccountModal
          account={editingAccount}
          form={accountForm}
          setForm={setAccountForm}
          onSubmit={editingAccount ? handleUpdateAccount : handleCreateAccount}
          onClose={() => {
            setShowAccountModal(false);
            setEditingAccount(null);
          }}
          users={users}
          loading={loading}
        />
      )}

      {showTradeModal && (
        <TradeModal
          mode={tradeMode}
          form={tradeForm}
          setForm={setTradeForm}
          onSubmit={handleTrade}
          onClose={() => setShowTradeModal(false)}
          accounts={userAccounts}
          stocks={stocks}
          loading={loading}
        />
      )}

      {showDepositModal && (
        <DepositModal
          account={selectedAccount}
          form={depositForm}
          setForm={setDepositForm}
          onSubmit={handleDeposit}
          onClose={() => setShowDepositModal(false)}
          loading={loading}
        />
      )}
    </div>
  );
}

